{{
    let createEntities = it.changes.filter(function(row){return (row.opt==='add'&&row['type']==='entity');});
    let dropEntities   = it.changes.filter(function(row){return (row.opt==='delete'&&row['type']==='entity');});
    let modifyEntities = it.changes.filter(function(row){return (row.opt==='update'&&row['type']==='entity');});

    function baseChangedDDL(beforeAfter){
        if(beforeAfter == null){
            return '';
        }
        let ret = [];
        let before = beforeAfter.before || {"defKey":"","defName":"","comment":""};
        let after = beforeAfter.after || {"defKey":"","defName":"","comment":""};
        if(before.defKey !== after.defKey){
            ret.push(`EXEC sp_rename '${before.defKey}','${after.defKey}'`);
        }
        let commentText = '';
        let commentChanged = false;
        if(before.defName !== after.defName){
            commentText = after.defName;
            commentChanged = true;
        }
        if(before.comment !== after.comment){
            commentChanged = true;
            if(commentText){
                commentText = (commentText+ ';'+after.comment)
            }else{
                commentText = after.comment
            }
        }
        if(commentChanged){
            let myText = `IF ((SELECT COUNT(*) FROM ::fn_listextendedproperty('MS_Description','SCHEMA', 'dbo','TABLE', '${after.defKey}', NULL, NULL)) > 0)
            \n\tEXEC sp_updateextendedproperty 'MS_Description', '${commentText}','SCHEMA', 'dbo','TABLE', '${after.defKey}'
            \nELSE
            \n\tEXEC sp_addextendedproperty 'MS_Description', '${commentText}', 'SCHEMA', 'dbo','TABLE', '${after.defKey}'
            `;
            ret.push(myText);
            /*ret.push('ALTER TABLE '+after.defKey+' COMMENT \''+commentText+'\'');*/
        }
        let baseText = '-- 基本信息:\n';
        return baseText+ret.join(';\n')+';';
    };

    function buildAddedDDL(entity){
        let ret = [];
        let fieldAdded = entity.data.fieldAdded||[];
        if(fieldAdded.length == 0){
            return '';
        }

        let firstDDL = `ALTER TABLE [dbo].[${entity.data.baseInfo.defKey}]`;
        for (let field of fieldAdded) { 
            let ddlItem = `ADD [${field.defKey}] ${field.dbType}`;
            /*处理数据类型长度*/
            if(field.len>0){
                ddlItem += ('('+field.len);
                if(parseInt(field.scale)>0){
                    ddlItem += (','+field.scale);
                }
                ddlItem += ')';
            }
            let defaultValue = field.defaultValue;
            defaultValue = (defaultValue==null)?"":(""+defaultValue);
            if(defaultValue.length>0){
                ddlItem += (' DEFAULT ' + defaultValue);
            }
            if(field.notNull){
                ddlItem += ' NOT NULL';
            }
            ret.push(`${firstDDL} ${ddlItem}`);

            /*处理字段注释*/
            let fieldComments = [];
            if(field.defName != null &&field.defName.length>0){
                fieldComments.push(field.defName);
            }
            if(field.comment != null &&field.comment.length>0){
                fieldComments.push(field.comment);
            }
            let commentText = fieldComments.join(';');
            if(commentText != null && commentText.length > 0){
                let commentDDL = `EXEC sp_addextendedproperty 'MS_Description', N'${commentText}','SCHEMA', N'dbo','TABLE', N'${entity.data.baseInfo.defKey}','COLUMN', N'${field.defKey}'`;
                ret.push(commentDDL);
            }
        }
        return '\n'+ret.join(';\n');
    };

    function buildRemovedDDL(entity){
        let ret = [];
        let fieldRemoved = entity.data.fieldRemoved||[];
        if(fieldRemoved.length == 0){
            return '';
        }

        let firstDDL = `ALTER TABLE [dbo].[${entity.data.baseInfo.defKey}]`;
        for (let field of fieldRemoved) { 
            ret.push(`${firstDDL} DROP COLUMN [${field.defKey}]`);
        }
        return '\n'+ret.join(';\n');
    };

    function buildModifiedDDL(entity){
        let ret = [];
        let fieldModified = entity.data.fieldModified||[];

        let firstDDL = `ALTER TABLE [dbo].[${entity.data.baseInfo.defKey}]`;
        for (let field of fieldModified) { 
            let changeDDL = '';
            let before = field.before || {};
            let after = field.after || {};
            if(before.defKey === after.defKey){
                changeDDL += (' ALTER COLUMN ['+after.defKey+']');
            }else{
                let renameText = `EXEC sp_rename '[dbo].[${entity.data.baseInfo.defKey}].[${before.defKey}]','${after.defKey}','COLUMN';`;
                ret.push(renameText);
                continue;
            }
            changeDDL += (' '+after.dbType);
            if(after.len>0){
                changeDDL += ('('+after.len);
                if(parseInt(after.scale)>0){
                    changeDDL += (','+after.scale);
                }
                changeDDL += ')';
            }
            let defaultValue = after.defaultValue;
            defaultValue = (defaultValue==null)?"":(""+defaultValue);
            if(defaultValue.length>0){
                changeDDL += (' DEFAULT ' + defaultValue);
            }

            if(after.notNull){
                changeDDL += ' NOT NULL';
            }
            ret.push(`${firstDDL} ${changeDDL};`);
        }
        return ret;
    };
}}
