function formatGoLang(str, fieldName, field, fileds, emptLength, isFiled) {
    var maxLength = 0;

    if (isFiled == 1) {
        for (var i = 0; i < fileds.length; i++) {
            if (getBlength(it.func.camel(fileds[i].defKey, true)) > maxLength) {
                maxLength = getBlength(it.func.camel(fileds[i].defKey, true)) + 2;
            }
        }
    } else if (isFiled == 2) {
        for (var i = 0; i < fileds.length; i++) {
            var newStr = "gorm:column:" + fileds[i].defKey + " json:" + it.func.camel(fileds[i].defKey, true);
            if (getBlength(newStr) > maxLength) {
                maxLength = getBlength(newStr) + 2;
            }
        }
        var empt = "";
        var strLength = getBlength(str);
        if (field.primaryKey) {
            strLength += getBlength("primaryKey;");
        }
        for (var j = 0; j < maxLength - strLength; j++) {
            empt += ' ';
        }
        return empt;
    } else if (isFiled == 3) {
        /*获取某个字段的最大长度*/
        for (var i = 0; i < fileds.length; i++) {
            var newStr = eval("fileds[" + i + "]." + fieldName);
            if (getBlength(newStr) > maxLength) {
                maxLength = getBlength(newStr) + 1;
            }
        }
    } else if (isFiled == 4) {
        /*获取某个字段的最大长度*/
        for (var i = 0; i < fileds.length; i++) {
            var newStr = fileds[i].comment + ";" + fileds[i].defName;
            if (getBlength(newStr) > maxLength) {
                maxLength = getBlength(newStr) + 1;
            }
        }
    }
    else {
        maxLength = emptLength;
    }

    var strLength = getBlength(str);
    for (var j = 0; j < maxLength - strLength; j++) {
        str += ' ';
    }
    return str;
}

function getBlength(str) {
    var n = 0;
    for (var i = str.length; i--;) {
        n += str.charCodeAt(i) > 255 ? 2 : 1;
    }
    return n;
} 

